## This file is not part of Octave.## -*- texinfo -*-## @deftypefn {Function File} [@var{alpha}] = }detectaVermelhoVerdeImagem (@var{pathArq})## ## A barra para inserir o caminho no pathArq é " / "## Aluna Jacqueline Abreu - 2011.1## @end deftypefn##, function detectaVermelhoVerdeImagem(pathImage)    img_detectar=imread(pathImage);    img_detectar=rgb2hsv(img_detectar);    tamanho_img= size(img_detectar);            for linha = 1:tamanho_img        for col = 1:tamanho_img               if  (                (( (img_detectar(linha,col,1) > 0.03333) && (img_detectar(linha,col,1) < 0.14999 ))                                     ||                 ( (img_detectar(linha, col,1)>0.1638) && (img_detectar(linha, col, 1) < 0.1640))                                    ||                 ((img_detectar(linha, col, 1)> 0.2777) && (img_detectar(linha,col,1) < 0.96222 ) ))                  ||( img_detectar(linha,col,2)<0.3695) || (img_detectar(linha, col, 3)>0.50)                )                    img_detectar(linha, col, 3) = 0;                    img_detectar(linha, col, 1) = 0;                    img_detectar(linha, col, 2) = 0;            endif        endfor    endfor    imshow(hsv2rgb(img_detectar));        endfunction    
